import React from "react";
import { Link } from "react-router-dom";
import { useDispatch } from "react-redux";
import { deleteProduct } from "../../redux/actions/productActions";

const Product = (props) => {
  const { product } = props;
  const dispatch = useDispatch();

  const deletehandler = (id) => {
    if (window.confirm("Tem certeza que deseja deletar o produto?")) {
      dispatch(deleteProduct(id));
    }
  };

  return (
    <>
      <div className="col-md-6 col-sm-6 col-lg-3 mb-5">
        <div className="card card-product-grid shadow-sm">
          <Link to="#" className="img-wrap"><img src={product.image} alt="Produto" /></Link>
          <div className="info-wrap">
            <Link to="#" className="title text-truncate">{product.name}</Link>
            <div className="price mb-2">${product.price}</div>
            <div className="row">
              <Link to={`/product/${product._id}/edit`} className="btn btn-sm btn-outline-success p-2 pb-3 col-md-6">
                <i className="fas fa-pen"></i>
              </Link>
              <Link to="#" onClick={() => deletehandler(product._id)} className="btn btn-sm btn-outline-danger p-2 pb-3 col-md-6">
                <i className="fas fa-trash-alt"></i>
              </Link>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Product;
