import { useState, useEffect, Fragment } from 'react';
import { Link, useParams } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { verifyEmail } from '../../redux/actions/userActions';
import Header from '../../components/Header';

const VerificaEmail = () => {
    const [urlValida, setUrlValida] = useState(false);
    const {id, token} = useParams();
    const dispatch = useDispatch();

    useEffect(() => {
        const verificaEmailUrl = () => {
            try {
                console.log(id);
                dispatch(verifyEmail(id, token));
                setUrlValida(true);
            } catch (error) {
                console.log(error);
                setUrlValida(false);
            }
        };

        verificaEmailUrl();
    }, [id, token, dispatch]);

    return (
        <div>
            <Header />
            <Fragment>
                {urlValida ? (
                    <div>
                        <h1>E-mail verificado com sucesso</h1>
                        <Link to='/login'>
                            <button>Login</button>
                        </Link>
                    </div>
                ) : (
                    <h1>Não encontrado</h1>
                )}
            </Fragment>
        </div>
    );
}

export default VerificaEmail;
