import React from "react";

const Rating = ({ value, text }) => {

    let sFirstClassName  = value >= 1 ? "fas fa-star" : value >= 0.5 ? "fas fa-star-half-alt" : "far fa-star";
    let sSecondClassName = value >= 2 ? "fas fa-star" : value >= 1.5 ? "fas fa-star-half-alt" : "far fa-star";
    let sThirdClassName  = value >= 3 ? "fas fa-star" : value >= 2.5 ? "fas fa-star-half-alt" : "far fa-star";
    let sFourthClassName = value >= 4 ? "fas fa-star" : value >= 3.5 ? "fas fa-star-half-alt" : "far fa-star";
    let sFifthClassName  = value >= 5 ? "fas fa-star" : value >= 4.5 ? "fas fa-star-half-alt" : "far fa-star";

    return (
        <div className="rating">
            <i className={ sFirstClassName  }></i>
            <i className={ sSecondClassName }></i>
            <i className={ sThirdClassName  }></i>
            <i className={ sFourthClassName }></i>
            <i className={ sFifthClassName  }></i>

            <span>{ text && text }</span>
        </div>
    );
};

export default Rating;
