import React from "react";

const CalltoActionSection = () => {
    return (
        <div className="subscribe-section bg-white-black">
            <div className="container">
                <div className="row">
                    <div className="col-xs-12">
                        <div className="subscribe-head">
                            <h2>Precisa de mais dicas?</h2>
                            <p>Cadastre-se gratuitamente e obtenha as dicas mais recentes</p>
                            <form action="" className="form-section">
                                <input placeholder="Seu e-mail..." name="email" type="email" />
                                <input value="Sim, eu quero!" name="inscrever" type="submit" />
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CalltoActionSection;
