import { useState, useEffect, Fragment } from 'react';
import { Link, useParams } from 'react-router-dom';
import PinInput from 'react-pin-input';
import Header from '../../components/Header';

const verificaEmail = () => {
    const [urlValida, setUrlValida] = useState(false);
    const param = useParams();

    useEffect(() => {
        const verificaEmailUrl = async() => {
            try {
                const url = `http://localhost:5000/api/users/${param.id}/verify/${param.token}`;
                // TODO const { data } = await axios.get(url);
            } catch (error) {
                console.log(error);
                setUrlValida(false);
            }
        }
    })

    return (
        <Fragment>
            {urlValida ? (
                <div>
                    <h1>E-mail verificado com sucesso</h1>
                    <Link to='/login'>
                        <button>Login</button>
                    </Link>
                </div>
            ) : (
                <h1>Não encontrado</h1>
            )}
        </Fragment>
    );
}

export default verificaEmail;
