import { useState, useEffect, Fragment } from 'react';
import { Link, useParams } from 'react-router-dom';
import { useDispatch } from 'react-redux';
import { verifyEmail } from '../../redux/actions/userActions';

const VerificaEmail = () => {
    const [urlValida, setUrlValida] = useState(false);
    const param = useParams();
    const dispatch = useDispatch();

    useEffect(() => {
        const verificaEmailUrl = async() => {
            try {
                dispatch(verifyEmail(param.id, param.token));
                setUrlValida(true);
            } catch (error) {
                console.log(error);
                setUrlValida(false);
            }
        };

        verificaEmailUrl();
    }, [param, dispatch]);

    return (
        <Fragment>
            {urlValida ? (
                <div>
                    <h1>E-mail verificado com sucesso</h1>
                    <Link to='/login'>
                        <button>Login</button>
                    </Link>
                </div>
            ) : (
                <h1>Não encontrado</h1>
            )}
        </Fragment>
    );
}

export default VerificaEmail;
